import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { addToCart } from '../actions/cartActions';
import Message from '../Components/Message';

export default function CartScreen(props) {
  const productId = props.match.params.id;
  const qty = props.location.search
    ? Number(props.location.search.split('=')[1])
    : 1;
    const cart = useSelector((state) => state.cart);
    const { cartItems } = cart;

    const dispatch = useDispatch();
    useEffect(() => {
      if (productId) {
        dispatch(addToCart(productId, qty));
      }
    }, [dispatch, productId, qty]);
    const removeFromCartHandler = (id) => {
      // delete action
    };
  
    const checkoutHandler = () => {
      props.history.push('/signin?redirect=shipping');
    };
  return (
    <>
   <div className='section-padding cart-page'>
     ]<div className='container'>
       <div className='row'>
         <div className='col-md-12'>
     <div className='card mt-2'>

     <h5 classN ="card-header">My Cart (Design Two)<span class="text-success float-right">(5 item)</span></h5>


         <div className='card-body pt-0 pr-0 pl-0 pb-0'>

           <div className='cart-list-product'>
           <a className="float-right remove-cart" href="#"><i className="fas fa-times"></i></a>
           <img className="img-fluid" src="../Images/7.jpg" alt=""></img>
           <span className="badge badge-success">50% OFF</span>
           <h5><a href="#">Product Title Here</a></h5>
           <h6><strong><span className="mdi mdi-approval"></span> Available in</strong> - 500 gm</h6>
           <p className="offer-price mb-0">$450.99 <i className="fas fa-tag"></i> <span className="regular-price">$800.99</span></p>
           </div>

         
         </div>
         <div className='card-footer cart-sidebar-footer'>

         <div className="card-footer cart-sidebar-footer">
<div className="cart-store-details">
<p>Sub Total <strong class="float-right">$900.69</strong></p>
<p>Delivery Charges <strong className="float-right text-danger">+ $29.69</strong></p>
<h6>Your total savings <strong className="float-right text-danger">$55 (42.31%)</strong></h6>
</div>
<a href="checkout.html"><button className="btn btn-secondary btn-lg btn-block text-left" type="button"><span className="float-left"><i class="mdi mdi-cart-outline"></i> Proceed to Checkout </span><span class="float-right"><strong>$1200.69</strong> <span class="mdi mdi-chevron-right"></span></span></button></a>
</div>
         </div>
       </div>
     </div>
         </div> 
       </div>
     </div>
 
    </>
  );
}
