import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { addToCart } from '../actions/cartActions';
import Message from '../Components/Message';

export default function CartScreen(props) {
  const productId = props.match.params.id;
  const qty = props.location.search
    ? Number(props.location.search.split('=')[1])
    : 1;
    const cart = useSelector((state) => state.cart);
    const { cartItems } = cart;

    const dispatch = useDispatch();
    useEffect(() => {
      if (productId) {
        dispatch(addToCart(productId, qty));
      }
    }, [dispatch, productId, qty]);
    const removeFromCartHandler = (id) => {
      // delete action
    };
  
    const checkoutHandler = () => {
      props.history.push('/signin?redirect=shipping');
    };
  return (
    <>
   <div className='section-padding cart-page'>
     ]<div className='container'>
       <div className='row'>
         <div className='col-md-12'>
     <div className='card mt-2'>
     <h5 class="card-header">My Cart (Design Two)<span class="text-secondary float-right">(5 item)</span></h5>
         <div className='card-body pt-0 pr-0 pl-0 pb-0'>
           <div className='cart-list-product'>
           <a className="float-right remove-cart" href="#"><i className="fas fa-times"></i></a>
           <img className="img-fluid" src="../Images/7.jpg" alt=""></img>
           <span className="badge badge-success">50% OFF</span>
           <h5><a href="#">Product Title Here</a></h5>
           <h6><strong><span className="mdi mdi-approval"></span> Available in</strong> - 500 gm</h6>
           <p className="offer-price mb-0">$450.99 <i className="fas fa-tag"></i> <span className="regular-price">$800.99</span></p>
           </div>
         </div>
         <div className='card-footer cart-sidebar-footer'></div>
       </div>
     </div>
         </div> 
       </div>
     </div>
 
    </>
  );
}
